import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import { useWallet } from '../hooks/useWallet';

export const TokenDetailPage: React.FC = () => {
  const { tokenId } = useParams<{ tokenId: string }>();
  const { isConnected } = useWallet();
  const [purchaseAmount, setPurchaseAmount] = useState('');
  const [isPurchasing, setIsPurchasing] = useState(false);

  // Mock token data with neobrutalist vibes
  const token = {
    id: tokenId,
    name: 'DOGE KILLER',
    symbol: 'DOGEK',
    description: 'THE MOST BRUTAL MEMECOIN ON STELLAR. DOGE IS DEAD. LONG LIVE DOGEK! ðŸ”¥ðŸ’€',
    currentPrice: '0.00001234',
    marketCap: '1,234,567',
    volume24h: '420,690',
    priceChange24h: 420.69,
    holderCount: 1337,
    launchProgress: 85,
    isLaunched: false,
    totalSupply: '1,000,000,000',
    tokensSold: '850,000,000',
    xlmRaised: '8.5',
    launchThresholdXlm: '10',
    creatorAddress: 'GXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXX',
    contractAddress: 'CXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXX',
    createdAt: new Date().toISOString(),
  };

  const handlePurchase = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!isConnected) {
      alert('CONNECT YOUR WALLET FIRST, ANON!');
      return;
    }

    if (!purchaseAmount || parseFloat(purchaseAmount) <= 0) {
      alert('ENTER A VALID XLM AMOUNT, DEGEN!');
      return;
    }

    setIsPurchasing(true);
    try {
      console.log('PURCHASING TOKENS WITH XLM:', purchaseAmount);
      
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 2000));
      
      alert('PURCHASE SUCCESSFUL! WELCOME TO THE MOON MISSION! ðŸš€');
      setPurchaseAmount('');
    } catch (error) {
      console.error('Error purchasing tokens:', error);
      alert('PURCHASE FAILED! TRY AGAIN, ANON!');
    } finally {
      setIsPurchasing(false);
    }
  };

  const calculateTokensReceived = (xlmAmount: string): string => {
    if (!xlmAmount || parseFloat(xlmAmount) <= 0) return '0';
    
    const xlm = parseFloat(xlmAmount);
    const price = parseFloat(token.currentPrice);
    return (xlm / price).toLocaleString();
  };

  return (
    <div className="neo-grid">
      {/* Token Header */}
      <div className="neo-card neo-card-yellow p-8 neo-rotate-1">
        <div className="neo-flex justify-between items-start mb-6">
          <div>
            <h1 className="neo-title text-4xl mb-2">{token.name}</h1>
            <p className="neo-subtitle text-2xl text-gray-700">${token.symbol}</p>
            {token.description && (
              <p className="neo-text font-mono mt-4 text-lg">{token.description}</p>
            )}
          </div>
          
          <div className={`px-6 py-3 border-4 border-black font-mono font-bold text-lg ${
            token.isLaunched 
              ? 'bg-green-400 text-black' 
              : 'bg-pink-400 text-white'
          }`}>
            {token.isLaunched ? 'ðŸš€ LAUNCHED ON DEX' : 'ðŸ“ˆ BONDING CURVE ACTIVE'}
          </div>
        </div>

        {/* Key Metrics */}
        <div className="grid grid-cols-2 md:grid-cols-4 gap-6">
          <div className="text-center">
            <div className="neo-title text-3xl text-blue-600">{token.currentPrice}</div>
            <div className="neo-text font-mono">XLM PRICE</div>
          </div>
          <div className="text-center">
            <div className="neo-title text-3xl text-green-600">{token.marketCap}</div>
            <div className="neo-text font-mono">MARKET CAP</div>
          </div>
          <div className="text-center">
            <div className="neo-title text-3xl text-purple-600">{token.volume24h}</div>
            <div className="neo-text font-mono">24H VOLUME</div>
          </div>
          <div className="text-center">
            <div className="neo-title text-3xl text-orange-600">{token.holderCount}</div>
            <div className="neo-text font-mono">HOLDERS</div>
          </div>
        </div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* Purchase Widget */}
        <div className="lg:col-span-1">
          <div className="neo-card neo-card-pink p-6 sticky top-4 neo-rotate-2">
            <h2 className="neo-subtitle text-2xl mb-6 text-white">
              {token.isLaunched ? 'TRADE ON DEX' : 'BUY TOKENS'}
            </h2>
            
            {token.isLaunched ? (
              <div className="text-center py-8">
                <p className="neo-text text-white mb-6 font-mono">
                  THIS TOKEN HAS LAUNCHED AND IS NOW TRADING ON THE STELLAR DEX.
                </p>
                <button className="neo-btn neo-btn-success w-full neo-shake">
                  TRADE ON STELLAR DEX
                </button>
              </div>
            ) : (
              <form onSubmit={handlePurchase} className="space-y-6">
                <div>
                  <label className="neo-subtitle text-lg mb-3 block text-white">
                    XLM AMOUNT
                  </label>
                  <input
                    type="number"
                    value={purchaseAmount}
                    onChange={(e) => setPurchaseAmount(e.target.value)}
                    className="neo-input w-full"
                    placeholder="0.1"
                    min="0.0000001"
                    step="0.0000001"
                  />
                </div>

                {purchaseAmount && (
                  <div className="neo-card p-4 bg-black text-white">
                    <div className="neo-text font-mono text-sm">YOU WILL RECEIVE:</div>
                    <div className="neo-subtitle text-xl">
                      {calculateTokensReceived(purchaseAmount)} {token.symbol}
                    </div>
                  </div>
                )}

                <button
                  type="submit"
                  disabled={!isConnected || isPurchasing || !purchaseAmount}
                  className="w-full neo-btn neo-btn-primary neo-shake"
                >
                  {isPurchasing ? (
                    <div className="neo-flex items-center justify-center">
                      <div className="w-6 h-6 border-4 border-black border-t-transparent rounded-full animate-spin mr-3"></div>
                      <span>PURCHASING...</span>
                    </div>
                  ) : (
                    'BUY TOKENS NOW'
                  )}
                </button>

                {!isConnected && (
                  <p className="text-center neo-text font-mono text-white">
                    CONNECT WALLET TO PURCHASE
                  </p>
                )}
              </form>
            )}
          </div>
        </div>

        {/* Charts and Info */}
        <div className="lg:col-span-2 space-y-8">
          {/* Launch Progress */}
          {!token.isLaunched && (
            <div className="neo-card neo-card-cyan p-6 neo-rotate-1">
              <h3 className="neo-subtitle text-2xl mb-6">LAUNCH PROGRESS</h3>
              
              <div className="space-y-6">
                <div>
                  <div className="neo-flex justify-between mb-3">
                    <span className="neo-text font-mono">XLM RAISED: {token.xlmRaised} / {token.launchThresholdXlm}</span>
                    <span className="neo-text font-mono font-bold">{token.launchProgress}%</span>
                  </div>
                  <div className="neo-progress">
                    <div 
                      className="neo-progress-bar" 
                      style={{ width: `${token.launchProgress}%` }}
                    />
                  </div>
                </div>

                <div className="grid grid-cols-2 gap-6">
                  <div>
                    <div className="neo-text font-mono text-sm">TOKENS SOLD</div>
                    <div className="neo-subtitle">{token.tokensSold} / {token.totalSupply}</div>
                  </div>
                  <div>
                    <div className="neo-text font-mono text-sm">REMAINING TO LAUNCH</div>
                    <div className="neo-subtitle">{(parseFloat(token.launchThresholdXlm) - parseFloat(token.xlmRaised)).toFixed(1)} XLM</div>
                  </div>
                </div>
              </div>
            </div>
          )}

          {/* Price Chart Placeholder */}
          <div className="neo-card p-6 neo-rotate-3">
            <h3 className="neo-subtitle text-2xl mb-6">PRICE CHART</h3>
            <div className="h-64 bg-yellow-400 border-4 border-black flex items-center justify-center neo-rotate-1">
              <div className="text-center">
                <div className="text-6xl mb-4">ðŸ“ˆ</div>
                <div className="neo-subtitle">CHART COMING SOON</div>
                <div className="neo-text font-mono">ONLY UP TECHNOLOGY</div>
              </div>
            </div>
          </div>

          {/* Token Information */}
          <div className="neo-card p-6 neo-rotate-2">
            <h3 className="neo-subtitle text-2xl mb-6">TOKEN INFORMATION</h3>
            
            <div className="space-y-4">
              <div className="neo-flex justify-between">
                <span className="neo-text font-mono">TOTAL SUPPLY:</span>
                <span className="neo-text font-mono font-bold">{token.totalSupply} {token.symbol}</span>
              </div>
              <div className="neo-flex justify-between">
                <span className="neo-text font-mono">TOKENS SOLD:</span>
                <span className="neo-text font-mono font-bold">{token.tokensSold} {token.symbol}</span>
              </div>
              <div className="neo-flex justify-between">
                <span className="neo-text font-mono">XLM RAISED:</span>
                <span className="neo-text font-mono font-bold">{token.xlmRaised} XLM</span>
              </div>
              <div className="neo-flex justify-between">
                <span className="neo-text font-mono">LAUNCH THRESHOLD:</span>
                <span className="neo-text font-mono font-bold">{token.launchThresholdXlm} XLM</span>
              </div>
              <div className="neo-flex justify-between">
                <span className="neo-text font-mono">CREATOR:</span>
                <span className="neo-text font-mono text-xs">{token.creatorAddress.slice(0, 8)}...{token.creatorAddress.slice(-8)}</span>
              </div>
              <div className="neo-flex justify-between">
                <span className="neo-text font-mono">CONTRACT:</span>
                <span className="neo-text font-mono text-xs">{token.contractAddress.slice(0, 8)}...{token.contractAddress.slice(-8)}</span>
              </div>
              <div className="neo-flex justify-between">
                <span className="neo-text font-mono">CREATED:</span>
                <span className="neo-text font-mono font-bold">{new Date(token.createdAt).toLocaleDateString()}</span>
              </div>
            </div>
          </div>

          {/* Recent Transactions Placeholder */}
          <div className="neo-card neo-card-yellow p-6 neo-rotate-1">
            <h3 className="neo-subtitle text-2xl mb-6">RECENT TRANSACTIONS</h3>
            <div className="text-center py-12">
              <div className="text-6xl mb-4">ðŸ“‹</div>
              <div className="neo-subtitle mb-2">TRANSACTION HISTORY</div>
              <div className="neo-text font-mono">COMING SOON TO A BLOCKCHAIN NEAR YOU</div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};
