import React, { useState, useEffect } from 'react';

export const FreighterDebug: React.FC = () => {
  const [freighterStatus, setFreighterStatus] = useState<{
    detected: boolean;
    location?: string;
    version?: string;
    isAllowed?: boolean;
    error?: string;
  }>({ detected: false });

  useEffect(() => {
    const checkFreighter = async () => {
      try {
        // Wait a bit for extension to load
        await new Promise(resolve => setTimeout(resolve, 1000));
        
        let freighterApi = null;
        let location = '';
        
        // Check multiple possible locations
        if (window.freighter) {
          freighterApi = window.freighter;
          location = 'window.freighter';
        } else if (window.freighterApi) {
          freighterApi = window.freighterApi;
          location = 'window.freighterApi';
        } else if ((window as any).stellar) {
          freighterApi = (window as any).stellar;
          location = 'window.stellar';
        }
        
        if (freighterApi) {
          try {
            const isAllowed = await freighterApi.isAllowed();
            setFreighterStatus({
              detected: true,
              location,
              isAllowed,
            });
          } catch (error: any) {
            setFreighterStatus({
              detected: true,
              location,
              error: error.message,
            });
          }
        } else {
          // Log all available window properties for debugging
          const windowProps = Object.keys(window).filter(key => 
            key.toLowerCase().includes('freighter') || 
            key.toLowerCase().includes('stellar')
          );
          
          setFreighterStatus({
            detected: false,
            error: `Not found. Available props: ${windowProps.join(', ') || 'none'}`
          });
        }
      } catch (error: any) {
        setFreighterStatus({
          detected: false,
          error: error.message
        });
      }
    };

    checkFreighter();
  }, []);

  if (process.env.NODE_ENV === 'production') {
    return null; // Don't show in production
  }

  return (
    <div className="fixed bottom-4 right-4 bg-gray-800 text-white p-4 rounded-lg text-sm max-w-xs">
      <h4 className="font-bold mb-2">Freighter Debug</h4>
      <div className="space-y-1">
        <div>Detected: {freighterStatus.detected ? '✅' : '❌'}</div>
        {freighterStatus.location && (
          <div className="text-xs">Location: {freighterStatus.location}</div>
        )}
        {freighterStatus.isAllowed !== undefined && (
          <div>Allowed: {freighterStatus.isAllowed ? '✅' : '❌'}</div>
        )}
        {freighterStatus.error && (
          <div className="text-red-300 text-xs">Error: {freighterStatus.error}</div>
        )}
        <div className="text-xs text-gray-400 mt-2">
          Check browser console for more details
        </div>
      </div>
    </div>
  );
};
