import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import { useWallet } from '../hooks/useWallet';

export const TokenDetailPage: React.FC = () => {
  const { tokenId } = useParams<{ tokenId: string }>();
  const { isConnected } = useWallet();
  const [purchaseAmount, setPurchaseAmount] = useState('');
  const [isPurchasing, setIsPurchasing] = useState(false);

  // Mock token data
  const token = {
    id: tokenId,
    name: 'Moon Coin',
    symbol: 'MOON',
    description: 'A token that aims for the moon! ðŸš€',
    currentPrice: '0.001234',
    marketCap: '50,000',
    volume24h: '5,000',
    priceChange24h: 15.5,
    holderCount: 42,
    launchProgress: 65,
    isLaunched: false,
    totalSupply: '1,000,000',
    tokensSold: '650,000',
    xlmRaised: '6.5',
    launchThresholdXlm: '10',
    creatorAddress: 'GXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXX',
    contractAddress: 'CXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXX',
    createdAt: new Date().toISOString(),
  };

  const handlePurchase = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!isConnected) {
      alert('Please connect your wallet first');
      return;
    }

    if (!purchaseAmount || parseFloat(purchaseAmount) <= 0) {
      alert('Please enter a valid XLM amount');
      return;
    }

    setIsPurchasing(true);
    try {
      // TODO: Implement purchase logic
      console.log('Purchasing tokens with XLM:', purchaseAmount);
      
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 2000));
      
      alert('Purchase successful! (This is a demo)');
      setPurchaseAmount('');
    } catch (error) {
      console.error('Error purchasing tokens:', error);
      alert('Failed to purchase tokens');
    } finally {
      setIsPurchasing(false);
    }
  };

  const calculateTokensReceived = (xlmAmount: string): string => {
    if (!xlmAmount || parseFloat(xlmAmount) <= 0) return '0';
    
    // Simple calculation for demo (in real app, this would use bonding curve)
    const xlm = parseFloat(xlmAmount);
    const price = parseFloat(token.currentPrice);
    return (xlm / price).toFixed(2);
  };

  return (
    <div className="max-w-7xl mx-auto neo-grid">
      {/* Token Header */}
      <div className="neo-card p-8 transform rotate-neo--1">
        <div className="neo-flex justify-between items-start mb-8 flex-wrap">
          <div>
            <h1 className="neo-title text-5xl mb-4">{token.name}</h1>
            <p className="neo-subtitle text-2xl mb-4">${token.symbol}</p>
            {token.description && (
              <p className="neo-text font-neo-mono text-lg">{token.description}</p>
            )}
          </div>
          
          <div className={`neo-card p-4 transform rotate-neo-2 ${
            token.isLaunched 
              ? 'bg-neo-green' 
              : 'bg-neo-blue'
          }`}>
            <span className="neo-subtitle text-lg text-neo-white">
              {token.isLaunched ? 'ðŸš€ LAUNCHED ON DEX' : 'ðŸ“ˆ BONDING CURVE ACTIVE'}
            </span>
          </div>
        </div>

        {/* Key Metrics */}
        <div className="grid grid-cols-2 md:grid-cols-4 gap-6">
          <div className="neo-card neo-card-yellow p-6 text-center transform rotate-neo-1">
            <div className="neo-title text-3xl mb-2">{token.currentPrice} XLM</div>
            <div className="neo-subtitle text-sm">CURRENT PRICE</div>
          </div>
          <div className="neo-card neo-card-cyan p-6 text-center transform rotate-neo--1">
            <div className="neo-title text-3xl mb-2">{token.marketCap} XLM</div>
            <div className="neo-subtitle text-sm">MARKET CAP</div>
          </div>
          <div className="neo-card neo-card-pink p-6 text-center transform rotate-neo-2">
            <div className="neo-title text-3xl mb-2 text-neo-white">{token.volume24h} XLM</div>
            <div className="neo-subtitle text-sm text-neo-white">24H VOLUME</div>
          </div>
          <div className="neo-card p-6 text-center bg-neo-orange transform rotate-neo--2">
            <div className="neo-title text-3xl mb-2 text-neo-white">{token.holderCount}</div>
            <div className="neo-subtitle text-sm text-neo-white">HOLDERS</div>
          </div>
        </div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* Purchase Widget */}
        <div className="lg:col-span-1">
          <div className="neo-card p-6 transform rotate-neo-1 sticky top-4">
            <h2 className="neo-subtitle text-2xl mb-6">
              {token.isLaunched ? 'TRADE ON DEX' : 'BUY TOKENS'}
            </h2>
            
            {token.isLaunched ? (
              <div className="text-center py-8">
                <p className="neo-text font-neo-mono mb-6">
                  THIS TOKEN HAS LAUNCHED AND IS NOW TRADING ON THE STELLAR DEX.
                </p>
                <button className="neo-btn neo-btn-primary w-full">
                  TRADE ON STELLAR DEX
                </button>
              </div>
            ) : (
              <form onSubmit={handlePurchase} className="space-y-6">
                <div>
                  <label className="neo-subtitle text-lg mb-3 block">
                    XLM AMOUNT
                  </label>
                  <input
                    type="number"
                    value={purchaseAmount}
                    onChange={(e) => setPurchaseAmount(e.target.value)}
                    className="neo-input w-full"
                    placeholder="0.1"
                    min="0.0000001"
                    step="0.0000001"
                  />
                </div>

                {purchaseAmount && (
                  <div className="neo-card neo-card-yellow p-4 transform rotate-neo--1">
                    <div className="neo-text font-neo-mono mb-2">YOU WILL RECEIVE:</div>
                    <div className="neo-subtitle text-xl">
                      {calculateTokensReceived(purchaseAmount)} {token.symbol}
                    </div>
                  </div>
                )}

                <button
                  type="submit"
                  disabled={!isConnected || isPurchasing || !purchaseAmount}
                  className={`neo-btn neo-btn-primary w-full ${isPurchasing ? 'neo-shake' : ''}`}
                >
                  {isPurchasing ? (
                    <div className="neo-flex justify-center items-center">
                      <div className="w-5 h-5 border-4 border-neo-black border-t-transparent rounded-full animate-spin mr-3"></div>
                      <span>PURCHASING...</span>
                    </div>
                  ) : (
                    'BUY TOKENS'
                  )}
                </button>

                {!isConnected && (
                  <p className="neo-text font-neo-mono text-center">
                    CONNECT WALLET TO PURCHASE
                  </p>
                )}
              </form>
            )}
          </div>
        </div>

        {/* Charts and Info */}
        <div className="lg:col-span-2 space-y-8">
          {/* Launch Progress */}
          {!token.isLaunched && (
            <div className="neo-card neo-card-cyan p-6 transform rotate-neo--1">
              <h3 className="neo-subtitle text-2xl mb-6">LAUNCH PROGRESS</h3>
              
              <div className="space-y-6">
                <div>
                  <div className="neo-flex justify-between neo-text font-neo-mono mb-3">
                    <span>XLM RAISED: {token.xlmRaised} / {token.launchThresholdXlm}</span>
                    <span>{token.launchProgress}%</span>
                  </div>
                  <div className="neo-progress">
                    <div 
                      className="neo-progress-bar"
                      style={{ width: `${token.launchProgress}%` }}
                    ></div>
                  </div>
                </div>

                <div className="grid grid-cols-2 gap-6">
                  <div className="neo-card p-4 bg-neo-yellow">
                    <div className="neo-subtitle text-sm mb-2">TOKENS SOLD</div>
                    <div className="neo-text font-neo-mono font-bold">{token.tokensSold} / {token.totalSupply}</div>
                  </div>
                  <div className="neo-card p-4 bg-neo-pink">
                    <div className="neo-subtitle text-sm mb-2 text-neo-white">REMAINING TO LAUNCH</div>
                    <div className="neo-text font-neo-mono font-bold text-neo-white">{(parseFloat(token.launchThresholdXlm) - parseFloat(token.xlmRaised)).toFixed(1)} XLM</div>
                  </div>
                </div>
              </div>
            </div>
          )}

          {/* Price Chart Placeholder */}
          <div className="neo-card p-6 bg-neo-green transform rotate-neo-2">
            <h3 className="neo-subtitle text-2xl mb-6">PRICE CHART</h3>
            <div className="h-64 neo-border bg-neo-white flex items-center justify-center">
              <div className="text-center">
                <div className="text-6xl mb-4">ðŸ“ˆ</div>
                <div className="neo-subtitle text-xl">PRICE CHART COMING SOON</div>
              </div>
            </div>
          </div>

          {/* Token Information */}
          <div className="neo-card neo-card-pink p-6 transform rotate-neo--2">
            <h3 className="neo-subtitle text-2xl mb-6 text-neo-white">TOKEN INFORMATION</h3>
            
            <div className="space-y-4 neo-text font-neo-mono text-neo-white">
              <div className="neo-flex justify-between">
                <span>TOTAL SUPPLY:</span>
                <span className="bg-neo-white text-neo-black px-2 py-1 border-2 border-neo-black">{token.totalSupply} {token.symbol}</span>
              </div>
              <div className="neo-flex justify-between">
                <span>TOKENS SOLD:</span>
                <span className="bg-neo-yellow text-neo-black px-2 py-1 border-2 border-neo-black">{token.tokensSold} {token.symbol}</span>
              </div>
              <div className="neo-flex justify-between">
                <span>XLM RAISED:</span>
                <span className="bg-neo-cyan text-neo-black px-2 py-1 border-2 border-neo-black">{token.xlmRaised} XLM</span>
              </div>
              <div className="neo-flex justify-between">
                <span>LAUNCH THRESHOLD:</span>
                <span className="bg-neo-green text-neo-black px-2 py-1 border-2 border-neo-black">{token.launchThresholdXlm} XLM</span>
              </div>
              <div className="neo-flex justify-between">
                <span>CREATOR:</span>
                <span className="bg-neo-white text-neo-black px-2 py-1 border-2 border-neo-black font-neo-mono text-xs">{token.creatorAddress.slice(0, 8)}...{token.creatorAddress.slice(-8)}</span>
              </div>
              <div className="neo-flex justify-between">
                <span>CONTRACT:</span>
                <span className="bg-neo-white text-neo-black px-2 py-1 border-2 border-neo-black font-neo-mono text-xs">{token.contractAddress.slice(0, 8)}...{token.contractAddress.slice(-8)}</span>
              </div>
              <div className="neo-flex justify-between">
                <span>CREATED:</span>
                <span className="bg-neo-white text-neo-black px-2 py-1 border-2 border-neo-black">{new Date(token.createdAt).toLocaleDateString()}</span>
              </div>
            </div>
          </div>

          {/* Recent Transactions Placeholder */}
          <div className="neo-card p-6 bg-neo-orange transform rotate-neo-1">
            <h3 className="neo-subtitle text-2xl mb-6 text-neo-white">RECENT TRANSACTIONS</h3>
            <div className="text-center py-12">
              <div className="text-8xl mb-6">ðŸ“‹</div>
              <div className="neo-subtitle text-xl text-neo-white">TRANSACTION HISTORY COMING SOON</div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};
