import React, { useState, useEffect } from 'react';

export const FreighterDebug: React.FC = () => {
  const [freighterStatus, setFreighterStatus] = useState<{
    detected: boolean;
    version?: string;
    isAllowed?: boolean;
    error?: string;
  }>({ detected: false });

  useEffect(() => {
    const checkFreighter = async () => {
      try {
        // Wait a bit for extension to load
        await new Promise(resolve => setTimeout(resolve, 1000));
        
        if (window.freighter) {
          try {
            const isAllowed = await window.freighter.isAllowed();
            setFreighterStatus({
              detected: true,
              isAllowed,
            });
          } catch (error: any) {
            setFreighterStatus({
              detected: true,
              error: error.message,
            });
          }
        } else {
          setFreighterStatus({
            detected: false,
            error: 'Freighter not found on window object'
          });
        }
      } catch (error: any) {
        setFreighterStatus({
          detected: false,
          error: error.message
        });
      }
    };

    checkFreighter();
  }, []);

  if (process.env.NODE_ENV === 'production') {
    return null; // Don't show in production
  }

  return (
    <div className="fixed bottom-4 right-4 bg-gray-800 text-white p-4 rounded-lg text-sm max-w-xs">
      <h4 className="font-bold mb-2">Freighter Debug</h4>
      <div className="space-y-1">
        <div>Detected: {freighterStatus.detected ? '✅' : '❌'}</div>
        {freighterStatus.isAllowed !== undefined && (
          <div>Allowed: {freighterStatus.isAllowed ? '✅' : '❌'}</div>
        )}
        {freighterStatus.error && (
          <div className="text-red-300">Error: {freighterStatus.error}</div>
        )}
        <div className="text-xs text-gray-400 mt-2">
          Check browser console for more details
        </div>
      </div>
    </div>
  );
};
