import React, { useState, useEffect } from 'react';
import { useWallet } from '../hooks/useWallet';
import { contractService, TokenInfo, BondingCurveInfo } from '../services/ContractService';

interface TokenTradingCardProps {
  tokenId: string;
}

export const TokenTradingCard: React.FC<TokenTradingCardProps> = ({ tokenId }) => {
  const { isConnected, publicKey, signTransaction } = useWallet();
  const [tokenInfo, setTokenInfo] = useState<TokenInfo | null>(null);
  const [loading, setLoading] = useState(true);
  const [trading, setTrading] = useState(false);
  const [xlmAmount, setXlmAmount] = useState('');

  useEffect(() => {
    loadTokenData();
  }, [tokenId]);

  const loadTokenData = async () => {
    setLoading(true);
    try {
      const token = await contractService.getTokenInfo(tokenId);
      setTokenInfo(token);
    } catch (error) {
      console.error('Error loading token data:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleTrade = async () => {
    if (!isConnected || !publicKey || !tokenInfo || !curveInfo) {
      alert('Please connect your wallet first');
      return;
    }

    if (tradeType === 'buy' && !xlmAmount) {
      alert('Please enter XLM amount');
      return;
    }

    if (tradeType === 'sell' && !tokenAmount) {
      alert('Please enter token amount');
      return;
    }

    setTrading(true);
    try {
      let transactionXDR: string;

      if (tradeType === 'buy') {
        const xlm = parseFloat(xlmAmount);
        transactionXDR = await contractService.buyTokens(tokenAddress, xlm, publicKey);
      } else {
        const tokens = parseFloat(tokenAmount);
        transactionXDR = await contractService.sellTokens(tokenAddress, tokens, publicKey);
      }

      const signedXDR = await signTransaction(transactionXDR);
      
      if (signedXDR) {
        console.log('Trade transaction signed:', signedXDR);
        alert(`${tradeType === 'buy' ? 'Buy' : 'Sell'} order submitted successfully!`);
        
        // Reload token data
        await loadTokenData();
        
        // Clear form
        setXlmAmount('');
        setTokenAmount('');
      }
    } catch (error) {
      console.error('Error executing trade:', error);
      alert(`Failed to ${tradeType}: ${error instanceof Error ? error.message : 'Unknown error'}`);
    } finally {
      setTrading(false);
    }
  };

  const calculateEstimate = () => {
    if (!curveInfo) return 0;

    if (tradeType === 'buy' && xlmAmount) {
      return contractService.calculateTokensForXLM(parseFloat(xlmAmount), curveInfo.currentPrice);
    } else if (tradeType === 'sell' && tokenAmount) {
      return contractService.calculateXLMForTokens(parseFloat(tokenAmount), curveInfo.currentPrice);
    }
    return 0;
  };

  if (loading) {
    return (
      <div className="neo-card p-6">
        <div className="neo-flex justify-center">
          <div className="w-8 h-8 border-4 border-neo-black border-t-transparent rounded-full animate-spin"></div>
        </div>
      </div>
    );
  }

  if (!tokenInfo || !curveInfo) {
    return (
      <div className="neo-card neo-card-red p-6">
        <p className="neo-text">Failed to load token information</p>
      </div>
    );
  }

  const estimate = calculateEstimate();

  return (
    <div className="neo-card p-6">
      <h3 className="neo-subtitle text-2xl mb-6">TRADE {tokenInfo.symbol}</h3>

      {/* Token Info */}
      <div className="neo-card neo-card-cyan p-4 mb-6">
        <div className="grid grid-cols-2 gap-4">
          <div>
            <p className="neo-text font-neo-mono">CURRENT PRICE</p>
            <p className="neo-subtitle text-lg">{curveInfo.currentPrice.toFixed(8)} XLM</p>
          </div>
          <div>
            <p className="neo-text font-neo-mono">TOTAL RAISED</p>
            <p className="neo-subtitle text-lg">{tokenInfo.totalRaised.toFixed(2)} XLM</p>
          </div>
          <div>
            <p className="neo-text font-neo-mono">PROGRESS</p>
            <p className="neo-subtitle text-lg">
              {((tokenInfo.totalRaised / curveInfo.graduationThreshold) * 100).toFixed(1)}%
            </p>
          </div>
          <div>
            <p className="neo-text font-neo-mono">STATUS</p>
            <p className={`neo-subtitle text-lg ${tokenInfo.isGraduated ? 'text-green-600' : 'text-yellow-600'}`}>
              {tokenInfo.isGraduated ? 'GRADUATED' : 'BONDING CURVE'}
            </p>
          </div>
        </div>
      </div>

      {/* Trading Interface */}
      {!tokenInfo.isGraduated && (
        <div className="space-y-6">
          {/* Trade Type Selector */}
          <div className="neo-flex gap-4">
            <button
              onClick={() => setTradeType('buy')}
              className={`neo-btn flex-1 ${tradeType === 'buy' ? 'neo-btn-success' : 'neo-btn-secondary'}`}
            >
              BUY
            </button>
            <button
              onClick={() => setTradeType('sell')}
              className={`neo-btn flex-1 ${tradeType === 'sell' ? 'neo-btn-danger' : 'neo-btn-secondary'}`}
            >
              SELL
            </button>
          </div>

          {/* Trade Form */}
          <div className="space-y-4">
            {tradeType === 'buy' ? (
              <div>
                <label className="neo-subtitle text-lg mb-3 block">
                  XLM AMOUNT
                </label>
                <input
                  type="number"
                  value={xlmAmount}
                  onChange={(e) => setXlmAmount(e.target.value)}
                  className="neo-input w-full"
                  placeholder="0.0"
                  min="0"
                  step="0.1"
                />
                {estimate > 0 && (
                  <p className="neo-text font-neo-mono mt-2">
                    â‰ˆ {estimate.toFixed(2)} {tokenInfo.symbol}
                  </p>
                )}
              </div>
            ) : (
              <div>
                <label className="neo-subtitle text-lg mb-3 block">
                  {tokenInfo.symbol} AMOUNT
                </label>
                <input
                  type="number"
                  value={tokenAmount}
                  onChange={(e) => setTokenAmount(e.target.value)}
                  className="neo-input w-full"
                  placeholder="0.0"
                  min="0"
                  step="1"
                />
                {estimate > 0 && (
                  <p className="neo-text font-neo-mono mt-2">
                    â‰ˆ {estimate.toFixed(6)} XLM
                  </p>
                )}
              </div>
            )}

            <button
              onClick={handleTrade}
              disabled={!isConnected || trading || (!xlmAmount && !tokenAmount)}
              className="neo-btn neo-btn-primary w-full text-lg py-4"
            >
              {trading ? (
                <div className="neo-flex justify-center">
                  <div className="w-6 h-6 border-4 border-neo-black border-t-transparent rounded-full animate-spin mr-4"></div>
                  <span>PROCESSING...</span>
                </div>
              ) : (
                `${tradeType.toUpperCase()} ${tokenInfo.symbol}`
              )}
            </button>

            {!isConnected && (
              <p className="neo-text font-neo-mono text-center">
                CONNECT YOUR WALLET TO TRADE
              </p>
            )}
          </div>
        </div>
      )}

      {/* Graduated Message */}
      {tokenInfo.isGraduated && (
        <div className="neo-card neo-card-green p-4">
          <p className="neo-subtitle text-lg mb-2">ðŸŽ‰ TOKEN GRADUATED!</p>
          <p className="neo-text font-neo-mono">
            This token has reached the graduation threshold and is now trading on Stellar DEX.
          </p>
        </div>
      )}
    </div>
  );
};
